//Jquery note//
<script src="js/jquery.min.js"></script>

//How to make it bounce -->
$("button").addClass("animated bounce");

//How to make it shake -->
$(".text-primary").addClass("animated shake");

//How to make it fadeout -->
$("#target6").addClass("animated fadeOut");

// How to make it primary-->
$("#target1").addClass("btn-primary")

// how to removeClass--->
$("#target2").removeClass("btn-default");

// how to change the css of an  element-->
$("#target1").css("color", "blue");

// how to adjust the properties of elements-->
$("button").prop("disabled", true);

// how to rewrite and emphasize the text-->
$("h3").html("<em>jQuery Playground</em>");

// to remove element from the page-->
$("#target4").remove();

// to move element -->
$("#target4").appendTo("#left-well");

// to append element-->
$("#target2").clone().appendTo("#right-well");

// to give the background color red-->
$("#left-well").parent().css("background-color", "blue")

// to change the font color-->
$("#right-well").children().css("color", "blue")

// to make it bounce '(3) means third class'-->
$(".target:nth-child(3)").addClass("animated bounce");

// Make target shake you can change odd or even-->
$(".target:even").addClass("animated shake");

// to make body drop-->
$("body").addClass("animated hinge");